sendfile.config:
sendfile on;
tcp_nopush on;
tcp_nodelay on;

basic_server.config:
server {
    listen 80;
    server_name example.com;
    root /var/www/html;
    index index.html;
}

regex_match.config:
location ~ \.php$ {
    # Corrispondenza con espressione regolare (distingue maiuscole da minuscole)
}

regex_case_insensitive.config:
location ~* \.\(jpg|jpeg|png|gif)$ {
    # Corrispondenza con espressione regolare (non distingue maiuscole da minuscole)
}

cors.config:
add_header Access-Control-Allow-Origin *;
add_header Access-Control-Allow-Methods "GET, POST, OPTIONS";
add_header Access-Control-Allow-Headers "DNT,User-Agent,X-Requested-With,Content-Type";

xss_protection.config:
add_header X-XSS-Protection "1; mode=block";
add_header X-Content-Type-Options nosniff;
add_header X-Frame-Options SAMEORIGIN;

rate_limit.config:
limit_req_zone $binary_remote_addr zone=one:10m rate=10r/s;

location /api/ {
    limit_req zone=one burst=20 nodelay;
}

fastcgi_cache.config:
fastcgi_cache_path /var/cache/nginx/fastcgi levels=1:2 keys_zone=fastcgi:10m;

location ~ \.php$ {
    fastcgi_cache fastcgi;
    fastcgi_cache_valid 200 1h;
}
